import logo from './logo.svg';
import './App.css';
import metronome from './public/drum_kit/Metronome.wav';

const DrumSounds = [
                    {
                       keyCode: 13,
                       key: 'Enter',
                       id: 'Metronome',
                       url: 'public/drum_kit/Metronome.wav'
                    }
                  ];

const DrumTrigger = () => {
    return <button>
       <audio src='public/drum_kit/Metronome.m4a' />
    </button>
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1 className="app-title">I am Drum Machine [┐∵]┘</h1>

        <div id="DrumTrigger"> <DrumTrigger /> </div>

        <p>
          En tempo-baserad trummaskin
        </p>
        <a
          className="app-link"
          href="https://ha-an.se"
          target="_blank"
        >
          Link to HA-AN
        </a>
      </header>
    </div>
  );
}

export default App;
