import logo from './logo.svg';
import './App.css';
//import MetronomeSound from '/public/drum_kit/Metronome.mp3';

function App() {
    async function playAudio() {
      var audio = new Audio('/public/drum_kit/Metronome.mp3');
      audio.type = 'audio/wav';

      try {
        await audio.play();
        console.log('Playing...');
      } catch (err) {
        console.log('Failed to play...' + err);
      }
    }

    <a href="#" onclick="playAudio()">Play Audio</a>
}

export default App;
