import { Link } from 'react-router-dom';
import Axios from 'axios';

const Admin = () => {

    const [accountList, setAccoutList] = useState([])

    useEffect(()=> {
        Axios.get("http://localhost:3001/read").then((response)=>{
            setAccoutList(response.data)
        });
    }, []);

    return (
        <div className='container'>
            <header className="App-header">
                <h2>Admin Dashboard</h2>
            </header>
            <main className='container'>
            <h4>User Data: </h4>
            <input value={findUser} type="text" placeholder="Search username..." className={"input"}/>
                <div className='admin-container'>
                    {accountList.map((value, key) => {
                        return <div key={key} className="account"><h5>{"User ID: " + value.userId + " - User Name: " + value.uname + " - Email: " + value.email}</h5>
                        <button cursor="pointer">EDIT ACCOUNT</button><button>RESET PASSWORD</button><button style={ { color : 'red' } }>DELETE</button>
                        </div>
                    })}
                </div>
                <div className='container'>
                    <Link to='/'>Go Back</Link>
                </div>
            </main>
        </div>
    )
};
export default Admin;
