import React, { Component } from 'react';

class Counter extends Component {

    state = {
        playing : false,
        count : 60,
        bpm : 100,
        beatsPerMeasure: 4
    }

//Insert Timer här, som kan delas med nuvarande klick / minut

    render() { 
        return (
        <React.Fragment className="counter_button" id="counter_button">
            <span>{this.formatCount()}</span>
            <button>Increment</button>
        </React.Fragment>
        );
    }

    formatCount(){
        const { count } = this.state;
        return count <= 40 ? <p className="fade_volume" id="fade_volume">fade volume</p> : count; //Vi kan även retunera en plain text eller en funktion här!
    }
}

export default Counter;
