import React, { Component } from 'react';


class Counter extends Component {

    state = {
        playing : false,
        count : 60,
        bpm : 100,
        beatsPerMeasure: 4,
        imageUrl: "https://picsum.photos/200"
    }

    constructor() {
        console.log("What is the value of ", this);
    }

    stepClick() {
        console.log('stepClicked');
    }

//Insert Timer här, som kan delas med nuvarande klick / minut

    render() { 
        return (
        <React.Fragment>
            <span style={ { fontSize: 30} } className="bpm_low fade_volume">{this.formatCount()}</span>
            <button onClick={this.stepClick} className="button" id="button">Increment</button>
            <img src={this.state.imageUrl} alt="" />
        </React.Fragment>
        );
    }

    formatCount(){
        const { count } = this.state;
        return count <= 40 ? <p>fade volume</p> : count; //Vi kan även retunera en plain text eller en funktion här!
    }
}

export default Counter;
