import React from "react";
import Kick1 from './808_kit/Kick.wav';
/* import Snare1 from './808_kit/Clap.wav';
import Clap1 from './808_kit/Snare.wav'; */


const kit808 = [
    {
        keyCode: 81,
        key: 'Q',
        id: '808-BD',
        url: Kick1
    }/* ,
    {
        keyCode: 87,
        key: 'W',
        id: '808-SN',
        url: Snare1
    },
    {
        keyCode: 69,
        key: 'E',
        id: '808-CL',
        url: Clap1
    } */
];

const Keyboard = () => {
    return kit808.map(sample => {
        return <button> 
            <audio src={sample.url} />
            {sample.key}
        </button>
    })
}

const Drum_Machine = () => {
    return <div id="drum_machine"> 
        <Keyboard />
    </div>
}

export default Drum_Machine;

