import React from "react";
import Kick1 from './808_kit/Kick.wav';
import Snare1 from './808_kit/Clap.wav';
import Clap1 from './808_kit/Snare.wav';
import HHC1 from './808_kit/HH_Closed.wav';
import HHO1 from './808_kit/HH_Open.wav';
import Perc1 from './808_kit/Perc.wav';


const kit808 = [
    {
        keyCode: 81,
        key: 'Q',
        id: '808-BD',
        url: Kick1
    },
    {
        keyCode: 87,
        key: 'W',
        id: '808-SN',
        url: Snare1
    },
    {
        keyCode: 69,
        key: 'E',
        id: '808-CL',
        url: Clap1
    },
    {
        keyCode: 65,
        key: 'A',
        id: '808-HHC',
        url: HHC1
    },
    {
        keyCode: 83,
        key: 'S',
        id: '808-HHO',
        url: HHO1
    },
    {
        keyCode: 68,
        key: 'D',
        id: '808-PRC',
        url: Perc1
    }
];

const Keyboard = ({play}) => {
    return kit808.map(sample => {
        return <button className="drum-pad" onClick={() => play(sample.key)}> 
            <audio className="clip" id={sample.key} src={sample.url} />
            {sample.key}
        </button>
    })
}

const Drum_Machine = () => {
    const play = (key) => {
        const audio = document.getElementById(key)
        audio.currentTime = 0;
        audio.play()
    }

    return <div id="drum_machine"> 
        <Keyboard play={play} />
    </div>
}

export default Drum_Machine;

//ReactDOM.render(<Drum_Machine />, document.getElementById("app"));
